import React from "react";
import { Pie } from "react-chartjs-2";

function PortfolioChart(props) {
  const { stocks } = props;
  const stockNames = stocks.map((stock) => stock.symbol);
  const stockValue = stocks.map((stock) => stock.amount);

  const state = {
    labels: {stockNames},
    datasets: [
      {
        label: "# of votes",
        data: {stockValue},
        backgroundColor: colors,
        borderWidth: 1,
        cx: 400,
        cy: 400

      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        display: true,
        position: "bottom",
      },
      title: {
        text: "Portfolio",
        display: true,
        fontSize: 100,
      },
    },
  };

  return (
    <div className="pie">
      <Pie data={state} options={options} cx={800} cy={800} />
    </div>
  );
}

  export default PortfolioChart;
