import { useState, useEffect, useContext } from "react";
import PortfolioChart from "./PortfolioChart";
import NavBar from "./Navbar";
import "../styles/Portfolio.css";
import { Tooltip, Icon, Paper, makeStyles } from "@material-ui/core";
import grey from "@material-ui/core/colors/grey";
import HelpOutlineIcon from "@material-ui/icons/HelpOutline";
import { db, fire } from "../config/Firebase.js";
import { UserContext } from "./UserContext";

function Portfolio() {
  const currentUser = useContext(UserContext);
  const [stocks, setStocks] = useState([]);
  const [newStockName, setNewStockName] = useState(" ");
  const [newStockValue, setNewStockValue] = useState(" ");
  const [investmentValue, setNewInvestmetValue] = useState(0);
  const [portfolioData, setNewPortfolioData] = useState({
    stocks: stocks,
    investmentValue: investmentValue
  }
  );

  const updatePortfolio = () => {
    if (currentUser != null) {
      db.child("users")
        .child(currentUser.uid)
        .child("portfolio")
        .set({stocks: stocks,
          investmentValue: investmentValue})
    }
  };

  useEffect(() => {
    if (currentUser != null) {
      db.child("users")
        .child(currentUser.uid)
        .child("portfolio")
        .on("value", (snapshot) => {
          const array = snapshot.val();
          setNewInvestmetValue(array.investmentValue);
          setStocks(array.stocks);
    })
  }}, []);

  const handleAddStock = (event) => {
    event.preventDefault();
    const newStockList = [
      ...stocks,
      {
        symbol: newStockName,
        amount: newStockValue,
        color: getRandomColor(),
      },
    ];
    setStocks(newStockList);
    setNewPortfolioData({stocks: stocks, investmentValue: investmentValue})
    updatePortfolio();
  };

  const handleDeleteStock = (index) => {
    const newStockList = stocks.filter(
      (stock) => stocks.indexOf(stock) != index
    );
    setStocks(newStockList);
  };

  const handleReset = () => {
    setStocks([]);
  };

  const getTotalValue = () => {
    let total = 0;
    for (let index = 0; index < stocks.length; index++) {
      total += parseFloat(stocks[index].amount);
    }
    return total;
  };

  var cashHoldings = parseFloat(investmentValue) - getTotalValue();

  const useStyles = makeStyles((theme) => ({
    icon: {
      margin: theme.spacing(1),
    },

    customWidth: {
      width: 1200,
      fontSize: 15,
    },

    paper: {
      backgroundColor: grey[200],
      boxShadow:
        "0px 3px 3px -2px rgb(0 0 0 / 50%), 0px 3px 4px 0px rgb(0 0 0 / 50%), 0px 1px 8px 0px rgb(0 0 0 / 70%)",
      padding: "20px",
      margin: "20px",
      maxWidth: "100%",
      textAlign: "center",
    },
    greypaper: {
      backgroundColor: grey[300],
      boxShadow:
        "0px 3px 3px -2px rgb(0 0 0 / 50%), 0px 3px 4px 0px rgb(0 0 0 / 50%), 0px 1px 8px 0px rgb(0 0 0 / 70%)",
      padding: "20px",
      margin: "20px",
      maxWidth: "100%",
    },
  }));

  const classes = useStyles();

  const allWeather = (
    <ul>
