import React, { useContext } from 'react';
import { AppContext } from './AppContext';

const ExpenseTotal = () => {
	const { expenses } = useContext(AppContext);
	const[state, setState] = useState(props.state);
	const currentUser = useContext(UserContext);

	useEffect(() => {
		if (currentUser != null) {
		  db.child("users")
			.child(currentUser.uid)
			.child("budget")
			.child("budgettracker")
			.child("remaining")
			.on("value", (snapshot) => {
			const array = snapshot.val();
			setState(array);
		});
	  }}, []);

	const total = expenses.reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const needExpenses = expenses.filter((item) => item.category == 1 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const wantExpenses = expenses.filter((item) => item.category == 2 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const financialExpenses = expenses.filter((item) => item.category == 3 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	return (
		<div class='alert alert-primary p-4'>
			<span>Spent so far (Total): ${total}</span>
			<br></br>
			<span>Spent so far (Needs): ${needExpenses}</span>
			<br></br>
			<span>Spent so far (Wants): ${wantExpenses}</span>
			<br></br>
			<span>Financial Goals so far: ${financialExpenses}</span>
		</div>
	);
};

export default ExpenseTotal;
