import React from "react";
import { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

function CompoundGrowthCalculator() {
  const [initialAmt, setNewInitialAmt] = useState("0");
  const [yearlyContribution, setNewYearlyContribution] = useState("0");
  const [interestRate, setNewInterestRate] = useState("0");
  const [numOfYears, setNewNumOfYears] = useState("0");
  const [totalValue, setNewTotalValue] = useState("0");
  const yArr = [];
  yArr.push(0, initialAmt);

  const calculate = () => {
    var total = initialAmt;
    for (let index = 1; index <= numOfYears; index++) {
      total *= 1 + interestRate / 100;
      total += yearlyContribution;
      yArr.push(index, total);
      xArr
    }
    setNewTotalValue(Math.round(total * 100) / 100);
  };

  return (
    <>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "40vh",
        }}
      >
        <form>
          <TextField
            label="Initial Amount?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewInitialAmt(e.target.value)}
          />
          <TextField
            label="Number of Years?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewNumOfYears(e.target.value)}
          />
          <TextField
            label="Annual Interest rate?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewInterestRate(e.target.value / 100)}
          />
           <TextField
            label="Yearly Contribution?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewYearlyContribution(e.target.value)}
          />
          <br />
          <br />
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              calculate();
            }}>
            Calculate
          </Button>

          <label> Total Investment Value: {totalValue} </label>

          <br />
          <br />
        </form>
      </div>
    </>
  );
}

export default CompoundGrowthCalculator;
