import React from "react";
import { useState } from "react";
import BudgetChart from "./BudgetChart";
import NavBar from "./Navbar";
import "../styles/Budget.css";
import BudgetForm from "./BudgetForm";

function Budget() {

  const defaultState = {
    newIncome: 4000,
    newNeeds: 50,
    newWants: 30,
    newFinancialGoals: 20
  };
  const [state, setState] = React.useState(defaultState);


  return (
    <>
      <NavBar /> 
      <BudgetForm defaultState={defaultState} onUpdate={variables => setState(variables)}/>
      <BudgetChart {...state}/>


      <section className="description">
        <h2> 50-30-20 Budget Rule </h2>
        <p>
          Popularised by Elizabeth Warren, this rule acts as a rule of thumb in
          helping you to allocate your budget in three categories: Needs, Wants
          and Financial goals.{" "}
        </p>
        <h3> Needs </h3>
        <u1>
          <li>Rent</li>
          <li>Groceries</li>
        </u1>
        <h3> Wants </h3>
        <u1>
          <li>Entertainment Services </li>
          <li>Hobbies </li>
        </u1>
        <h3> Financial Goals </h3>
        <u1>
          <li>Savings </li>
          <li>Investment </li>
        </u1>
        <p>
          {" "}
          As with all budgetting rules out there, this is only a rough guide and
          you can make your own adjustments to better suit your needs!{" "}
        </p>
      </section>
    </>
  );
}

export default Budget;
