import React, { useContext, useState, useEffect } from 'react';
import { AppContext } from './AppContext';
import { db, fire } from "../config/Firebase.js";
import { UserContext} from "../components/UserContext";

const ExpenseTotal = () => {
	const { expenses } = useContext(AppContext);
	const total = expenses.reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const needExpenses = expenses.filter((item) => item.category == 1 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const wantExpenses = expenses.filter((item) => item.category == 2 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const financialExpenses = expenses.filter((item) => item.category == 3 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);
	const[state, setState] = useState({total, needExpenses, wantExpenses, financialExpenses});
	const currentUser = useContext(UserContext);

	useEffect(() => {
		if (currentUser != null) {
		  db.child("users")
			.child(currentUser.uid)
			.child("budget")
			.child("budgettracker")
			.child("expenses")
			.on("value", (snapshot) => {
			const array = snapshot.val();
			setState(array);
		});
	  }}, []);

	  const updateExpensesTotal = () => {
		// setState(variables)
		if (currentUser != null) {
		  db.child("users")
		.child(currentUser.uid)
		.child("budget")
		.child("budgettracker")
		.child("expensesTotal")
		.set(state)
		}

	  }


	return (
		<div class='alert alert-primary p-4'>
			{updateExpensesTotal()}
			<span>Spent so far (Total): ${state.total}</span>
			<br></br>
			<span>Spent so far (Needs): ${state.needExpenses}</span>
			<br></br>
			<span>Spent so far (Wants): ${state.wantExpenses}</span>
			<br></br>
			<span>Financial Goals so far: ${state.financialExpenses}</span>
		</div>
	);
};

export default ExpenseTotal;
