import { useState } from "react";
import PortfolioChart from "./PortfolioChart";

function Portfolio() {
  const [stocks, setStocks] = useState([]);
  const [newStockName, setNewStockName] = useState(" ");
  const [newStockValue, setNewStockValue] = useState(" ");
  const [investmentValue, setNewInvestmetValue] = useState(" ");
  const [colors, setNewColors] = useState([]);
  const handleAddStock = (event) => {
    event.preventDefault();
    const newStockList = [
      ...stocks,
      {
        symbol: newStockName,
        amount: newStockValue,
      },
    ];
    setStocks(newStockList);
    setNewColors(getRandomColor());
  };

  const cashHoldings =
    { investmentValue } -
    stocks.reduce(
      (prevValue, currentValue) => prevValue + currentValue.amount,
      0
    );

  return (
    <>
      <div>
        <label>Input your Investment Value:</label>
        <input
          type="text"
          value={investmentValue}
          onChange={(event) => setNewInvestmetValue(event.target.value)}
          readonly
          class="total-input form-control"
        />
        <h2> Add a Stock to your Portfolio! </h2>
        <form onSubmit={handleAddStock}>
          <label>
            Ticker Symbol:
            <input
              type="text"
              value={newStockName}
              onChange={(event) => {
                setNewStockName(event.target.value);
              }}
            />
          </label>
          <label>
            Amount invested:
            <input
              type="text"
              value={newStockValue}
              onChange={(event) => {
                setNewStockValue(event.target.value);
              }}
            />
          </label>
          <input type="submit" value="Add" />
        </form>
      </div>

      <div>
        <h2>Portfolio</h2>
        {stocks.length > 0 ? (
          <StockList stocks={stocks} setStocks={setStocks} />
        ) : (
          <p> No stocks added!</p>
        )}
      </div>

      <div>
        <h3>Cash Holdings:</h3>
        {cashHoldings >= 0 ? { cashHoldings } : <p>More Cash Needed!</p>}
      </div>
      <div>
        <PortfolioChart stocks ={stocks} colors={colors}/>
      </div>
    </>
  );
}

function StockList(props) {
  const { stocks, setStocks } = props;

  return (
    <table style={{ margin: "0 auto", width: "100%" , textAlign: "center"}}>
      <thead>
        <tr>
          <th>No.</th>
          <th>Holdings</th>
          <th>Value</th>
        </tr>
      </thead>
      <tbody>
        {stocks.map((stock, index) => (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{stock.symbol}</td>
            <td> {stock.amount}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}
function getRandomColor() {
  var letters = '0123456789ABCDEF'.split('');
  var color = '#';
  for (var i = 0; i < 6; i++ ) {
      color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

export default Portfolio;
