import { useState } from "react";

function IntrinsicValue() {
  const [netProfit, newNetProfit] = useState("0");
  const [depAndAmort, newDepAndAmort] = useState("0");
  const [increaseInWC, newIncreaseINWC] = useState("0");
  const [capitalExpenditure, newCapitalExpenditure] = useState("0");
  const [debtRepayment, newDebtRepayment] = useState("0");
  const [debtRaised, newDebtRaised] = useState("0");
  const [numOfShares, newNumOfShares] = useState("0");
  const [rateOfReturn, newRateOfReturn] = useState("0");
  const [fcfeGrowth, newFcfeGrowth] = useState("0");
  var fcfeY1 =
    parseFloat(netProfit) +
    parseFloat(depAndAmort) -
    parseFloat(increaseInWC) -
    parseFloat(capitalExpenditure) -
    parseFloat(debtRepayment) +
    parseFloat(debtRaised);
  const projectedFCFE = [];

  const calculateIntrinsicValue = () => {
    let intrinsicValue = 0;
    for (let index = 1; index <= 5; index++) {
      intrinsicValue +=
        (projectedFCFE[index - 1].value / Math.pow(1 + rateOfReturn / 100, index));
    }
    return intrinsicValue;
  };

  for (let index = 1; index <= 5; index++) {
    projectedFCFE.push({
      label: `${index}`,
      value: fcfeY1 * Math.pow(1 + fcfeGrowth / 100, index - 1),
    });
  }

  return (
      <>
    <div> All Amount in Millions

      <label>
        Net Profit after Tax ($)
        <input
          type="number"
          id="netProfit"
          name="netProfit"
          value={netProfit}
          onChange={(event) => newNetProfit(event.target.value)}
        />
      </label>
      <label>
        Depreciation and Amortisation
        <input
          type="number"
          id="depAndAmort"
          name="depAndAmort"
          value={depAndAmort}
          onChange={(event) => newDepAndAmort(event.target.value)}
        />
      </label>
      <label>
        Increase in Working Capital
        <input
          type="number"
          id="increaseInWC"
          name="increaseInWC"
          value={increaseInWC}
          onChange={(event) => newIncreaseINWC(event.target.value)}
        />
      </label>
      <label>
        Capital Expenditure ($)
        <input
          type="number"
          id="capitalExpenditure"
          name="capitalExpenditure"
          value={capitalExpenditure}
          onChange={(event) => newCapitalExpenditure(event.target.value)}
        />
      </label>
      <label>
        Debt Repayment on existing debt ($)
        <input
          type="number"
          id="debtRepayment"
          name="debtRepayment"
          value={debtRepayment}
          onChange={(event) => newDebtRepayment(event.target.value)}
        />
      </label>
      <label>
        Fresh Debt Raised ($)
        <input
          type="number"
          id="debtRaised"
          name="debtRaised"
          value={debtRaised}
          onChange={(event) => newDebtRaised(event.target.value)}
        />
      </label>
      <label>
        No. of Outstanding Shares
        <input
          type="number"
          id="numOfShares"
          name="numOfShares"
          value={numOfShares}
          onChange={(event) => newNumOfShares(event.target.value)}
        />
      </label>
      <label>
        Required rate of return (%)
        <input
          type="number"
          id="rateOfReturn"
          name="rateOfReturn"
          value={rateOfReturn}
          onChange={(event) => newRateOfReturn(event.target.value)}
        />
      </label>
      <label>
        FCFE Growth Rate (%)
        <input
          type="number"
          id="fcfeGrowth"
          name="fcfeGrowth"
          value={fcfeGrowth}
          onChange={(event) => newFcfeGrowth(event.target.value)}
        />
      </label>

      <label>
        FCFE Y1
        <input type="number" value={fcfeY1} />
      </label>
      </div>
      <div>
      <table>
        <thead>
          <tr>
            <th scope="col">Year</th>
            <th scope="col">Projected FCFE</th>
          </tr>
        </thead>
        <tbody>
          {projectedFCFE.map(({ label, value }) => (
            <tr key={label}>
              <td>{label}</td>
              <td>${value.toFixed(2)} </td>
            </tr>
          ))}
        </tbody>
      </table>
      <label>
        Terminal Value
        <input
          type="number"
          value= {projectedFCFE[4].value.toFixed(2) * (1 / (rateOfReturn / 100))}
        />
      </label>
      <label>
        Intrinsic Value For Company
        <input type="number" value={calculateIntrinsicValue() +
            (projectedFCFE[4].value.toFixed(2) * (1 / (rateOfReturn / 100))) / 
            Math.pow(1 / (rateOfReturn / 100),5)} />
      </label>
      <label>
        Intrinsic Value per Share
        <input type="number" value={calculateIntrinsicValue() +
            (projectedFCFE[4].value.toFixed(2) * (1 / (rateOfReturn / 100))) / 
            Math.pow(1 / (rateOfReturn / 100),5) / numOfShares} />
      </label>
    </div>
    </>
  );
}

export default IntrinsicValue;
