import React from "react";
import { Pie } from "react-chartjs-2";

function BudgetChart(props) {

  const {newIncome, newNeeds, newWants, newFinancialGoals} = props;

  const state = {
    labels: ["Needs", "Wants", "Financial Goals"],
    datasets: [
      {
        label: "# of votes",
        data: [newIncome * (newNeeds /100) ,newIncome * (newWants /100),newIncome * (newFinancialGoals /100) ],
        backgroundColor: ["#2adece", "#dd3b79", "#ff766b"],
        borderWidth: 1,
        cx: 400,
        cy: 400

      },
    ],
  };

  const options = {
    responsive: falsee,
    maintainAspectRatio: true,
    aspectRatio: 1.5,
    plugins: {
      legend: {
        display: true,
        position: "bottom",
      },
      title: {
        text: "Budget Pie Chart",
        display: false,
        fontSize: 100,
      },
    },
  };

  return (
    <div className="pie">
      <Pie data={state} options={options} cx={1000} cy={1000}  />
    </div>

  );
}



export default BudgetChart;
