import React from "react";
import { Pie } from "react-chartjs-2";
import { useState } from "react";

function PortfolioChart(props) {
  const { stocks} = props;
  const stockNames = stocks.map((stock) => stock.symbol);
  const stockValue = stocks.map((stock) => stock.amount);
//   const [colors, setNewColors] = useState([]);
  let colors = [];

  while(colors.length < stocks.length) {
   const newColors = colors;
   newColors.push(getRandomColor())
   colors = newColors;
  }

  const state = {
    labels: stockNames,
    datasets: [
      {
        label: "# of votes",
        data: stockValue,
        backgroundColor: colors,
        borderWidth: 1,
        cx: 400,
        cy: 400

      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        display: true,
        position: "bottom",
      },
      title: {
        text: "Portfolio",
        display: true,
        fontSize: 100,
      },
    },
  };

  return (
    <div className="pie">
      <Pie data={state} options={options} cx={800} cy={800} />
    </div>
  );
}

function getRandomColor() {
    var letters = '0123456789ABCDEF'.split('');
    var color = '#';
    for (var i = 0; i < 6; i++ ) {
        color += letters[Math.floor(Math.random() * 16)];
    }
    return color;
  }

  export default PortfolioChart;
