import React, { useContext, useState, useEffect } from 'react';
import { AppContext } from './AppContext';
import { db, fire } from "../config/Firebase.js";
import { UserContext} from "../components/UserContext";
import { IfFirebaseAuthed } from "@react-firebase/auth";
import ExpenseItem from './ExpenseItem';
import "bootstrap/dist/css/bootstrap.min.css";


function Summary (props){

    const { expenses } = useContext(AppContext);
	const { budget, needs, wants, financialGoals } = props;
	const[state, setState] = useState(expenses);
	const currentUser = useContext(UserContext);

    useEffect(() => {
		if (currentUser != null) {
		  db.child("users")
			.child(currentUser.uid)
			.child("budget")
			.child("budgettracker")
			.child("expenseTotal")
			.on("value", (snapshot) => {
			const array = snapshot.val();
			if (array != null) {
			setState(array.expenses);
			}
		});
	  }}, []);

	const totalExpenses = state.reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const needExpenses = state.filter((item) => item.category == 1 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const wantExpenses = state.filter((item) => item.category == 2 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const financialExpenses = state.filter((item) => item.category == 3 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

    const [filteredExpenses, setfilteredExpenses] = useState(state || []);

	useEffect(() => {
		setfilteredExpenses(state);
	}, [state]);

	const handleChange = (event) => {
		const searchResults = state.filter((filteredExpense) =>
			filteredExpense.name.toLowerCase().includes(event.target.value)
		);
		setfilteredExpenses(searchResults);

	};
	const handleReset = () => {
		if (window.confirm("Are you sure to reset?")) {
			setState([]);
		  if (currentUser != null) {
			db.child("users")
			.child(currentUser.uid)
			.child("budget")
			.child("budgettracker")
			.child("expenseTotal")
			.set([])
			}
		  }
		}

    return(
        <>
        {/* <div className='budget-summary'>
			<span>Budget: ${budget}</span>
			<br></br>
			<span> Needs: ${needs/100 * budget} </span>
			<br></br>
			<span> Wants: ${wants/100 * budget} </span>
			<br></br>
			<span> Financial Goals: ${financialGoals/100 * budget} </span>

			</div> */}
        <div class={`alert p-4 ${alertType}`}>
			<span>Remaining: ${budget- totalExpenses}</span>
			<br></br>
			<span> Needs: ${needs/100 * budget - needExpenses}</span>
			<br></br>
			<span> Wants: ${wants/100 * budget - wantExpenses}</span>
			<br></br>
			<span> Financial Goals: ${financialGoals/100 * budget - financialExpenses}</span>

		</div>
        <div class='alert alert-primary p-4'>
        <span>Spent so far (Total): ${totalExpenses}</span>
        <br></br>
        <span>Spent so far (Needs): ${needExpenses}</span>
        <br></br>
        <span>Spent so far (Wants): ${wantExpenses}</span>
        <br></br>
        <span>Financial Goals so far: ${financialExpenses}</span>
    </div>

    <input
				type='text'
				class='form-control mb-2 mr-sm-2'
				placeholder='Type to search...'
				onChange={handleChange}
			/>
			<ul class='list-group mt-3 mb-3'>
				{filteredExpenses.map((expense) => (
					<ExpenseItem
						id={expense.id}
						name={expense.name}
						cost={expense.cost}
					/>
				))}
				</ul>
			<IfFirebaseAuthed>
			<button className="reset-btn" onClick={HandleReset}>
				Reset
			</button>
			</IfFirebaseAuthed>
    </>

    )
}

export default Summary;
