import { useState } from "react";
import PortfolioChart from "./PortfolioChart";
import NavBar from "./Navbar";
import "../styles/Portfolio.css";

function Portfolio() {
  const [stocks, setStocks] = useState([]);
  const [newStockName, setNewStockName] = useState(" ");
  const [newStockValue, setNewStockValue] = useState(" ");
  const [investmentValue, setNewInvestmetValue] = useState(" ");
  const handleAddStock = (event) => {
    event.preventDefault();
    const newStockList = [
      ...stocks,
      {
        symbol: newStockName,
        amount: newStockValue,
        color: getRandomColor(),
      },
    ];
    setStocks(newStockList);
  };

  const handleReset = () =>{
    setStocks([]);
  }

  const getTotalValue = () => {
    let total = 0;
    for (let index = 0; index < stocks.length; index++) {
      total += parseFloat(stocks[index].amount);
    }
    return total;
  };

  var cashHoldings = parseFloat(investmentValue) - getTotalValue();

  return (
    <>
      <section className="header">
        <NavBar />
      </section>
      <div className="title">Portfolio Allocator</div>
      <div className = "portfolio-guide">
      A proper portfolio allocation is imperative for a solid invesment foundation. There are many financial instruments to invest in or for hedging and here are 
      two recommended portfolio strategies for beginners but do not that you do not have to follow strictly to the allocation as this is just a rough guide. Feel free
      to customise to your own preferences to achieve your own ideal allocation.
      Financial instruments to invest in are stocks, bonds, REITs, commodities, ETF, cryptocurrency and more. 
There are many portfolio allocation strategies and as an investor, you should experiment and find the best allocation model that suits your long-term goals.
      <br></br>
      <ol>
                <li>
                    <h3>All-Weather Portfolio</h3>
                    <p>This portfolio which was created by Ray Dalio, one of the most successful hedge fund manager, is meant to make money in any market conditions.</p>
                    <ul>
                        <li>30% total stock market</li>
                        <li>40% long-term bonds</li>
                        <li>15% intermediate bonds</li>
                        <li>7.5% commodities</li>
                        <li>7.5% gold</li>
                    </ul>
                </li>
            </ol>

            <ol>
                <li>
                    <h3>Ivy Portfolio</h3>
                    <p>Another portfolio allocation is called the Ivy portfolio that attempts to emulate the portfolio allocation strategy of Ivy league universities.</p>
                    <ul>
                        <li>20% US stocks</li>
                        <li>20% international stocks</li>
                        <li>20% bonds</li>
                        <li>20% commodities</li>
                        <li>20% REITs (Real Estate Investment Trust)</li>
                    </ul>
                </li>
            </ol>
        <br></br>

      </div>
      <table className="portfolio-table">
        <thead>
          <tr>
            <th>Inputs</th>
            <th>Pie Chart</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>
              <div>
                <label>
                  Input your Investment Value:
                  <input
                    className="portfolio"
                    type="number"
                    min="0"
                    placeholder="0"

                    value={investmentValue}
                    onChange={(event) =>{
                      setNewInvestmetValue(event.target.value)
                    }}

                  />
                  <span class="validity"></span>
                </label>

                <div id="term"> Add a Stock to your Portfolio!</div>
                <form onSubmit={handleAddStock}>
                  <label>
                    Ticker Symbol:
                    <input
                      className="portfolio"
                      type="text"
                      required
                      value={newStockName}
                      onChange={(event) => {
                        setNewStockName(event.target.value);
                      }}
                    />
                  </label>
                  <label>
                    Amount invested:
                    <input
                      className="portfolio"
                      type="number"
                      min="0"
                      placeholder="0"

                      value={newStockValue}
                      onChange={(event) => {
                        setNewStockValue(event.target.value);
                      }}
                    />
                    <span class="validity"></span>
                  </label>
                  <input className="add-btn" type="submit" value="Add" />
                </form>
              </div>
            </td>
            <td>
              {stocks.length === 0 ? (
                <p id="terms">Portfolio is Empty!</p>
              ) : (
                <>
                <PortfolioChart stocks={stocks} />
                <button className="delete-btn" onClick={() => handleReset()}>
                  Reset
                </button>
                </>
              )}
            </td>
          </tr>
        </tbody>
      </table>

      <div>
        <div id="terms">Portfolio</div>
        {stocks.length > 0 ? (
          <StockList stocks={stocks} setStocks={setStocks} />
        ) : (
          <p id="terms-1"> No stocks added!</p>
        )}
      </div>

      <div>
        <div id="terms">Cash Holdings:</div>
        <p id="terms">
          {cashHoldings >= 0 ? cashHoldings : <p> More Cash Needed!</p>}
        </p>
      </div>
    </>
  );
}

function StockList(props) {
  const { stocks, setStocks } = props;
  const handleDeleteStock = (index) => {
    const newStockList = stocks.filter((stock) => stocks.indexOf(stock) != index);
    setStocks(newStockList);
  };

  return (
    <>
      <table className="holdings-table">
        <thead>
          <tr>
            <th>No.</th>
            <th>Holdings</th>
            <th>Value</th>
          </tr>
        </thead>
        <tbody>
          {stocks.map((stock, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{stock.symbol}</td>
              <td> {stock.amount}</td>

              <button
                className="delete-btn"
                onClick={() => handleDeleteStock(index)}
              >
                Delete
              </button>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
function getRandomColor() {
  var letters = "0123456789ABCDEF".split("");
  var color = "#";
  for (var i = 0; i < 6; i++) {
    color += letters[Math.floor(Math.random() * 16)];
  }
  return color;
}

export default Portfolio;
