import React, { useContext, useState, useEffect } from 'react';
import { AppContext } from './AppContext';
import { db, fire } from "../config/Firebase.js";
import { UserContext} from "../components/UserContext";

const RemainingBudget = (props) => {
	const { expenses } = useContext(AppContext);
	const currentUser = useContext(UserContext);
	const {newIncome, newNeeds, newWants, newFinancialGoals} = props;

	const totalExpenses = expenses.reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const needExpenses = expenses.filter((item) => item.category == 1 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const wantExpenses = expenses.filter((item) => item.category == 2 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const financialExpenses = expenses.filter((item) => item.category == 3 ).reduce((total, item) => {
		return (total += item.cost);
	}, 0);

	const[expense, setExpenses] = useState({totalExpenses, needExpenses, wantExpenses, financialExpenses});

	useEffect(() => {
		if (currentUser != null) {
		  db.child("users")
			.child(currentUser.uid)
			.child("budget")
			.child("budgettracker")
			.child("remaining")
			.on("value", (snapshot) => {
			const array = snapshot.val();
			setExpenses(array);
		});
	  }}, []);

	  const updateRemaining = () => {
		if (currentUser != null) {
		  db.child("users")
			.child(currentUser.uid)
			.child("budget")
			.child("budgettracker")
			.child("remaining")
			.set(expense)
	  }}


	const alertType = totalExpenses > state.newIncome ? 'alert-danger' : 'alert-success';

	return (
		<div class={`alert p-4 ${alertType}`}>
			{updateRemaining()}
			<span>Remaining: ${state.newIncome - expense.totalExpenses}</span>
			<br></br>
			<span> Needs: ${state.newNeeds/100 * state.newIncome - expense.needExpenses}</span>
			<br></br>
			<span> Wants: ${state.newWants/100 * state.newIncome - expense.wantExpenses}</span>
			<br></br>
			<span> Financial Goals: ${state.newFinancialGoals/100 * state.newIncome - expense.financialExpenses}</span>

		</div>
	);
};

export default RemainingBudget;
