import React from "react";
import { Pie } from "react-chartjs-2";

function BudgetChart(props) {

  const {newIncome} = props;

  const state = {
    labels: ["Needs", "Wants", "Financial Goals"],
    datasets: [
      {
        label: "# of votes",
        data: [newIncome * 0.5,newIncome * 0.3,newIncome * 0.2],
        backgroundColor: ["#2adece", "#dd3b79", "#ff766b"],
        borderWidth: 1,
        cx: 400,
        cy: 200

      },
    ],
  };

  const options = {
    plugins: {
      legend: {
        display: true,
        position: "bottom",
      },
      title: {
        text: "Budget Pie Chart",
        display: true,
        fontSize: 100,
      },
    },
  };

  return (
    <div className="pie">
      <Pie data={state} options={options} cx={200} cy={200} />
    </div>

  );
}

export default BudgetChart;
