import { useState } from "react";
import PortfolioChart from "./PortfolioChart";
import NavBar from "./Navbar";
import "../styles/Portfolio.css";

function Portfolio() {
  const [stocks, setStocks] = useState([]);
  const [newStockName, setNewStockName] = useState(" ");
  const [newStockValue, setNewStockValue] = useState(" ");
  const [investmentValue, setNewInvestmetValue] = useState(" ");
  const handleAddStock = (event) => {
    event.preventDefault();
    const newStockList = [
      ...stocks,
      {
        symbol: newStockName,
        amount: newStockValue,
        color: getRandomColor(),
      },
    ];
    setStocks(newStockList);
  };

  var cashHoldings =
     investmentValue  -
    stocks.reduce(
      (prevValue, currentValue) => prevValue + currentValue.amount,
      0
    );

  return (
    <>
    <section className="header">
        <NavBar />
      </section>
      <div>
        <label className = "inputs">Input your Investment Value:
        <input
        className = "portfolio"
          type="number"
          value={investmentValue}
          min = "0"
          onChange={(event) => setNewInvestmetValue(event.target.value)}
          readonly
          class="total-input form-control"
        />
        <span class="validity"></span>
        </label>

        <h2 id = "instruction"> Add a Stock to your Portfolio! </h2>
        <form onSubmit={handleAddStock}>
          <label className = "inputs">
            Ticker Symbol:
            <input
            className = "portfolio"
              type="text"
              value={newStockName}
              onChange={(event) => {
                setNewStockName(event.target.value);
              }}
            />
          </label>
          <label className = "inputs">
            Amount invested:
            <input
            className = "portfolio"
              type="number"
              min = "0"
              value={newStockValue}
              onChange={(event) => {
                setNewStockValue(event.target.value);
              }}
            />
            <span class="validity"></span>
          </label>
          <input className = "portfolio-btn" type="submit" value="Add" />
        </form>
      </div>

      <div>
        <h2 id = "instruction">Portfolio</h2>
        {stocks.length > 0 ? (
          <StockList stocks={stocks} setStocks={setStocks} />
        ) : (
          <p> No stocks added!</p>
        )}
      </div>

      <div>
        <h3>Cash Holdings:</h3>
        {cashHoldings >= 0 ? ( cashHoldings ) :( <p>More Cash Needed!</p>)}
      </div>
      <div>
        <PortfolioChart stocks ={stocks} />
      </div>
    </>
  );
}

function StockList(props) {
  const { stocks, setStocks } = props;

  return (
    <>
    <table style={{ margin: "0 auto", width: "100%" , textAlign: "center"}}>
      <thead>
        <tr>
          <th>No.</th>
          <th>Holdings</th>
          <th>Value</th>
        </tr>
      </thead>
      <tbody>
        {stocks.map((stock, index) => (
          <tr key={index}>
            <td>{index + 1}</td>
            <td>{stock.symbol}</td>
            <td> {stock.amount}</td>
          </tr>
        ))}
      </tbody>
    </table>
    </>
  );
}
function getRandomColor() {
  var letters = '0123456789ABCDEF'.split('');
  var color = '#';
  for (var i = 0; i < 6; i++ ) {
      color += letters[Math.floor(Math.random() * 16)];
  }
  return color;

}

export default Portfolio;
