import React from "react";
import { useState } from "react";
import BudgetChart from "./BudgetChart";
import NavBar from "./Navbar";
import '../styles/Budget.css';

function Budget() {
  const [newIncome, setNewIncome] = useState(1000);
  const [newNeeds, setNewNeeds] = useState(50);
  const [newWants, setNewWants] = useState(30);
  const [newFinancialGoals, setNewFinancialGoals] = useState(20);
  var totalPercentage = newNeeds + newWants + newFinancialGoals;

  return (
    <>


      {/* <NavBar /> */}

      <h2 id="text">BUDGET PLANNER</h2>

      <body>


                    <h1>Budget Pie Chart</h1> 
                    {totalPercentage === 100 || (newNeeds < 0 || newNeeds > 100) || 
                    (newFinancialGoals < 0 || newFinancialGoals> 100) || (newIncome <= 0) ? (
                    <BudgetChart style = {{height: "1000px"}}
                      newIncome={newIncome}
                      newNeeds={newNeeds}
                      newWants={newWants}
                      newFinancialGoals={newFinancialGoals}
                    /> ) : (<p> Total Percentage is not 100%! </p>)}
                    <div className="chart-container">
                      <canvas id="myChart"></canvas>
                    </div>



                <div class="form-group">
                  <label>Input your Income:</label>
                  <input
                    type="number"
                    value={newIncome}
                    onChange={(event) => setNewIncome(event.target.value)}
                    readonly
                    class="total-input form-control"
                  />
                </div>
                Percentage of allocations in your Budget
                <div class="form-group">
                  <label>Input your % of Needs:</label>
                  <input
                    type="number"
                    value={newNeeds}
                    min = "0"
                    onChange={(event) => setNewNeeds(event.target.value)}
                    readonly
                    class="total-input form-control"
                  />
                </div>
                <div class="form-group">
                  <label>Input your % of Wants:</label>
                  <input
                    type="number"
                    value={newWants}
                    min = "0"
                    onChange={(event) => setNewWants(event.target.value)}
                    readonly
                    class="total-input form-control"
                  />
                </div>
                <div class="form-group">
                  <label>Input your % of Financial Goals:</label>
                  <input
                    type="number"
                    value={newFinancialGoals}
                    min = "0"
                    onChange={(event) =>
                      setNewFinancialGoals(event.target.value)
                    }
                    readonly
                    class="total-input form-control"
                  />
                </div>


      </body>

      <section className="description">
        <h2> 50-30-20 Budget Rule </h2>
        <p>
          Popularised by Elizabeth Warren, this rule acts as a rule of thumb in
          helping you to allocate your budget in three categories: Needs, Wants
          and Financial goals.{" "}
        </p>
        <h3> Needs </h3>
        <u1>
          <li>Rent</li>
          <li>Groceries</li>
        </u1>
        <h3> Wants </h3>
        <u1>
          <li>Entertainment Services </li>
          <li>Hobbies </li>
        </u1>
        <h3> Financial Goals </h3>
        <u1>
          <li>Savings </li>
          <li>Investment </li>
        </u1>
        <p>
          {" "}
          As with all budgetting rules out there, this is only a rough guide and
          you can make your own adjustments to better suit your needs!{" "}
        </p>
      </section>
    </>
  );
}

export default Budget;
