import React from "react";
import { useState } from "react";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import LineChart from "./LineChart";

function CompoundGrowthCalculator() {
  const [initialAmt, setNewInitialAmt] = useState("");
  const [yearlyContribution, setNewYearlyContribution] = useState("");
  const [interestRate, setNewInterestRate] = useState("");
  const [numOfYears, setNewNumOfYears] = useState("");
  const [totalValue, setNewTotalValue] = useState("");
  let yArr = [];
  let xArr = [];
  xArr.push(0);
  const calculate = () => {
    let total = initialAmt;
    yArr.push(total);
    for (let index = 1; index <= numOfYears; index++) {
      total *= (1 + interestRate);
      total += yearlyContribution;
      yArr.push(total);
      xArr.push(index);
    }
    setNewTotalValue(Math.round(total * 100) / 100);
  };

  return (
    <>
      <div
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          height: "40vh",
        }}
      >
        <form>
          <TextField
            label="Initial Amount?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewInitialAmt(e.target.value)}
          />
          <TextField
            label="Number of Years?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewNumOfYears(e.target.value)}
          />
          <TextField
            label="Annual Interest rate?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewInterestRate(e.target.value / 100)}
          />
           <TextField
            label="Yearly Contribution?"
            variant="outlined"
            type="number"
            onChange={(e) => setNewYearlyContribution(e.target.value)}
          />
          <br />
          <br />
          <Button
            variant="contained"
            color="primary"
            onClick={() => {
              calculate();
            }}>
            Calculate
          </Button>

          <label> Total Investment Value: {totalValue} </label>

          <br />
          <br />
        </form>
      </div>
      <LineChart xArr={xArr} yArr ={yArr}/>
      xArr
    </>
  );
}

export default CompoundGrowthCalculator;
